//SAMPLE RULE EXAMPLE
"use strict";
({ params, imports }) => {
  const programEncounter = params.entity;
  const scheduleBuilder = new imports.rulesConfig.VisitScheduleBuilder({
    programEncounter
  });
  const _ = imports.lodash;
  const moment = imports.moment;

  const encounterScheduler = [
        {"name": "HBNC 2","encName":'HBNC', "earliest": 3, "max": 5},
        {"name": "HBNC 3","encName":'HBNC', "earliest": 7, "max": 9},
        {"name": "HBNC 4","encName":'HBNC', "earliest": 14, "max": 16},
        {"name": "HBNC 5","encName":'HBNC', "earliest": 21, "max": 23},
        {"name": "HBNC 6","encName":'HBNC', "earliest": 28, "max": 30},
        {"name": "HBNC 7","encName":'HBNC', "earliest": 42, "max": 44},
        {"name": "HBYC 1","encName":'HBYC', "earliest": 90, "max": 92}
    ];

  const dateOfBirth = programEncounter.individual.dateOfBirth;
  const visitDate = programEncounter.encounterDateTime;
  const dateOfDeath = programEncounter.getObservationValue('ee91d2b0-c93c-4d31-8c48-58738df229ff');

  let schedule = _.chain(encounterScheduler).filter(e => moment(visitDate).isSameOrBefore(moment(dateOfBirth).add(e.earliest, 'days'), 'date') === true )
                  .filter(e => (programEncounter.programEnrolment.hasEncounter('HBNC', e.name)) === false)
                  .first()
                  .value();

   const getEnc = programEncounter.programEnrolment.getEncounters(true);
   const getAllCompletedEncounters = _.chain(getEnc)
  .map(e => e.name)
  .value();

  const comparisonFunction = () => {
      for ( let i=0; i<getAllCompletedEncounters.length; i++) {
          if ( getAllCompletedEncounters[i] === programEncounter.name ) return true;
      }
  }

//  console.log('getAllCompletedEncounters-------->',getAllCompletedEncounters);
//  console.log('!_.isEmpty(schedule)-------->',!_.isEmpty(schedule));
//  console.log('!comparisonFunction-------->',!comparisonFunction);

   if ( !_.isEmpty(schedule) && !dateOfDeath && comparisonFunction() ) {

   scheduleBuilder
            .add({
                name: schedule.name,
                encounterType: schedule.encName,
                earliestDate:  moment(dateOfBirth).add(schedule.earliest, 'days').toDate() ,
                maxDate: moment(dateOfBirth).add(schedule.max, 'days').toDate()
            });

   }



  return scheduleBuilder.getAll();
};
